# GENERATOR
# тХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХРтХР

def generate_audio():
    base_dir = os.path.join(os.path.dirname(os.path.abspath(__file__)), "audio")

    print("=" * 60)
    print(" VocalLab Audio Generator тАФ 4 Languages")
    print(" Hindi | English | Telugu | Tamil")
    print("=" * 60)

    total_files = 0
    total_size = 0

    for lang_code, lang_info in LANGUAGES.items():
        lang_name = lang_info["name"]
        tld = lang_info["tld"]

        # Create language directory
        lang_dir = os.path.join(base_dir, lang_code)
        os.makedirs(lang_dir, exist_ok=True)

        prompts = PROMPTS.get(lang_code, {})
        if not prompts:
            print(f"\n тЪая╕П  No prompts found for {lang_name} ({lang_code}), skipping...")
            continue

        print(f"\n ЁЯФК Generating {lang_name} ({lang_code}) audio files...")

        for prompt_key, prompt_text in prompts.items():
            filename = f"{prompt_key}.mp3"
            filepath = os.path.join(lang_dir, filename)

            try:
                tts = gTTS(text=prompt_text, lang=lang_code, tld=tld, slow=False)
                tts.save(filepath)

                file_size = os.path.getsize(filepath)
                total_size += file_size
                total_files += 1

                size_kb = file_size / 1024
                print(f"   тЬЕ {filename} ({size_kb:.0f} KB)")

            except Exception as e:
                print(f"   тЭМ {filename} FAILED: {e}")

    # тФАтФА Summary тФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФАтФА
    print(f"\n{'=' * 60}")
    print(f" тЬЕ Generated {total_files} audio files!")
    print(f" ЁЯТ╛ Total size: {total_size / 1024:.0f} KB ({total_size / (1024*1024):.1f} MB)")
    print(f"\n ЁЯУБ Directory structure:")
    print(f"    audio/")

    for lang_code, lang_info in LANGUAGES.items():
        lang_dir = os.path.join(base_dir, lang_code)
        if os.path.exists(lang_dir):
            file_count = len([f for f in os.listdir(lang_dir) if f.endswith('.mp3')])
            print(f"    тФЬтФАтФА {lang_code}/  ({lang_info['name']}: {file_count} files)")

    print(f"\n ЁЯОп Supported languages: {', '.join([l['name'] for l in LANGUAGES.values()])}")
    print(f"{'=' * 60}")


